CREATE TABLE Empleados (
    id_empleado SERIAL PRIMARY KEY,
    nombre VARCHAR(100),
    id_departamento INT,
    salario DECIMAL(10,2),
    fecha_ingreso DATE
);


CREATE TABLE Productos (
    id_producto SERIAL PRIMARY KEY,
    nombre_producto VARCHAR(100),
    id_categoria INT,
    precio DECIMAL(10,2),
    fecha_venta DATE
);



CREATE TABLE Clientes (
    id_cliente SERIAL PRIMARY KEY,
    nombre_cliente VARCHAR(100),
    fecha_registro DATE
);




CREATE TABLE Pedidos (
    id_pedido SERIAL PRIMARY KEY,
    id_cliente INT REFERENCES Clientes(id_cliente),
    fecha_pedido DATE,
    total DECIMAL(10,2),
	id_producto INT REFERENCES Productos (id_producto)
);



CREATE TABLE Calificaciones (
    id_calificacion SERIAL PRIMARY KEY,
    id_estudiante INT,
    id_curso INT,
    nota DECIMAL(10, 2),
    fecha_examen DATE
);



CREATE TABLE Direcciones (
    id_direccion SERIAL PRIMARY KEY,
    id_cliente INT REFERENCES Clientes(id_cliente),
    direccion VARCHAR(255),
    fecha_registro DATE
);


CREATE TABLE Facturas (
    id_factura SERIAL PRIMARY KEY,
    id_cliente INT REFERENCES Clientes(id_cliente),
    fecha_pago DATE,
    monto DECIMAL(10,2)
);


CREATE TABLE Bibliotecas (
    id_prestamo SERIAL PRIMARY KEY,
    id_libro INT,
    id_usuario INT,
    fecha_prestamo DATE
);


CREATE TABLE Vuelos (
    id_reserva SERIAL PRIMARY KEY,
    id_pasajero INT,
    id_vuelo INT,
    fecha_reserva DATE
);


CREATE TABLE Suscripciones (
    id_suscripcion SERIAL PRIMARY KEY,
    id_usuario INT,
    fecha_inicio DATE,
    monto_pago DECIMAL(10,2)
);


CREATE TABLE Cuentas_Bancarias (
    id_transaccion SERIAL PRIMARY KEY,
    id_cuenta INT,
    monto DECIMAL(10,2),
    fecha_transaccion DATE
);


CREATE TABLE Mensajes (
    id_mensaje SERIAL PRIMARY KEY,
    id_chat INT,
    id_usuario INT,
    contenido TEXT,
    fecha_envio TIMESTAMP
);


CREATE TABLE Universidad (
    id_inscripcion SERIAL PRIMARY KEY,
    id_estudiante INT,
    id_curso INT,
    fecha_inscripcion DATE
);


CREATE TABLE Ventas (
    id_venta SERIAL PRIMARY KEY,
    id_ciudad INT,
    id_producto INT REFERENCES Productos(id_producto),
    fecha_venta DATE
);


CREATE TABLE Rentas_Autos (
    id_renta SERIAL PRIMARY KEY,
    id_usuario INT,
    id_vehiculo INT,
    fecha_renta DATE
);


---EJERCICIO 1---
SELECT nombre,
       id_departamento, 
	   salario,
	   fecha_ingreso,
	   LAST_VALUE(salario)OVER(PARTITION BY id_departamento ORDER BY fecha_ingreso ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) AS ultimo_salario
FROM empleados;

