---EJERCICIO 3---
WITH ultima_compra AS (
     SELECT cl.nombre_cliente,
	        p.fecha_pedido,
	        EXTRACT(MONTH FROM p.fecha_pedido) AS mes,
			EXTRACT(YEAR FROM p.fecha_pedido) AS ao,
			p.total
	 FROM clientes cl
	 INNER JOIN pedidos p
	 ON cl.id_cliente = p.id_cliente
)
SELECT nombre_cliente,
       fecha_pedido,
       mes,
	   ao,
	   total,
	   LAST_VALUE(total)OVER(PARTITION BY mes, ao ORDER BY fecha_pedido DESC  ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) AS ultima_compra
FROM ultima_compra;

